import "./Actors.css";
import { useParams } from "react-router-dom";
import { img_300, noPicture } from "../../config/config";
import React, { useEffect, useState } from "react";
import axios from "axios";


const handleDragStart = (e) => e.preventDefault();

const Actor = () => {
    let { id } = useParams();
    const [content, setCredits] = useState([]);

    const fetchCredits = async () => {
        const { data } = await axios.get(
          `https://api.themoviedb.org/3/person/${id}?api_key=${process.env.REACT_APP_API_KEY}&language=en-US`
        );
        setCredits(data.cast);
      };
    
      useEffect(() => {
        fetchCredits();
        // eslint-disable-next-line
      }, []);


    

    return (
        <div>
            
            {content && (


            <div className="Item">
                    <img
                    src={content.profile_path ? `${img_300}/${content.profile_path}` : noPicture}
                    alt={content?.name}

                    onDragStart={handleDragStart}
                    className="Item__img"
                    />
                    <b className="Item__txt">{content?.name}</b>
                    
                    
            </div> 

            )}    

        </div>
        
    );
};

export default Actor;
