import "./Actors.css";
import { useParams } from "react-router-dom";
import { img_300, noPicture } from "../../config/config";
import React, { useEffect, useState } from "react";
import axios from "axios";



const Actor = () => {
    let { id } = useParams();
    const [content, setActor] = useState([]);

    const fetchActor = async () => {
        const { data } = await axios.get(`https://api.themoviedb.org/3/person/2441?api_key=a500ed6497632b594464be767b4d390d&language=en-US`)
    };
    console.log(data);

    setActor(data.results);

    useEffect(() => {
        fetchActor();
    }, []);

            

    return (
        <div>
            hello
            
            {content &&  content.map((c) => (


            <div className="Item">
                    <img
                    src={c.profile_path ? `${img_300}/${c.profile_path}` : noPicture}
                    alt={c?.name}
                    className="Item__img"
                    />
                    <b className="Item__txt">{c?.name}</b>
                    
                    
            </div> 

            ))}    

        </div>
        
    );
};

export default Actor;
